const PRODUCE=1;const TOILETRIES=2;const COOKING=3;const MEAT=4;const DAIRY=5;const CANNED=6;const BAKERY=7;const BEVERAGE=8;const FROZEN=9;let delimiter="###";window.onload=()=>{let sortButtonElement=document.querySelector("#sortButton");if(sortButtonElement){sortButtonElement.addEventListener("click",()=>{let inputText=document.querySelector("#inputTextArea").value;document.querySelector("#outputTextArea").value="";document.querySelector("#outputTextArea").value=group(inputText)})}let copyButtonElement=document.querySelector("#copyButton");if(copyButtonElement){copyButtonElement.addEventListener("click",()=>{navigator.clipboard.writeText(document.querySelector("#outputTextArea").value)})}let clearButtonElement=document.querySelector("#clearButton");if(clearButtonElement){clearButtonElement.addEventListener("click",()=>{if(confirm("Are you sure you would like to clear your list?")){document.querySelector("#inputTextArea").value="";document.querySelector("#outputTextArea").value=""}})}let selectDelimiterElement=document.querySelector("#delimiterSelect");if(selectDelimiterElement){selectDelimiterElement.addEventListener("change",()=>{delimiter=selectDelimiterElement.value})}};group=text=>{let groups={produce:`${delimiter} PRODUCE ${delimiter}`,toiletries:`${delimiter} TOILETRIES ${delimiter}`,cooking:`${delimiter} COOKING ${delimiter}`,meat:`${delimiter} MEAT ${delimiter}`,dairy:`${delimiter} DAIRY ${delimiter}`,canned:`${delimiter} CANNED ${delimiter}`,bakery:`${delimiter} BAKERY ${delimiter}`,beverage:`${delimiter} BEVERAGE ${delimiter}`,frozen:`${delimiter} FROZEN ${delimiter}`,misc:`${delimiter} MISC ${delimiter}`};let textArray=text.split(/[\n,]/);for(item of textArray){trimmedItem=item.trim();trimmedItemToLower=trimmedItem.toLowerCase();switch(items.get(trimmedItemToLower)){case PRODUCE:groups.produce+=`\n${trimmedItem}`;break;case TOILETRIES:groups.toiletries+=`\n${trimmedItem}`;break;case COOKING:groups.cooking+=`\n${trimmedItem}`;break;case MEAT:groups.meat+=`\n${trimmedItem}`;break;case DAIRY:groups.dairy+=`\n${trimmedItem}`;break;case CANNED:groups.canned+=`\n${trimmedItem}`;break;case BAKERY:groups.bakery+=`\n${trimmedItem}`;break;case BEVERAGE:groups.beverage+=`\n${trimmedItem}`;break;case FROZEN:groups.frozen+=`\n${trimmedItem}`;break;default:groups.misc+=`\n${trimmedItem}`;break}}let grouped=groups.produce+"\n\n"+groups.toiletries+"\n\n"+groups.cooking+"\n\n"+groups.meat+"\n\n"+groups.dairy+"\n\n"+groups.canned+"\n\n"+groups.bakery+"\n\n"+groups.beverage+"\n\n"+groups.frozen+"\n\n"+groups.misc;return grouped};const items=new Map([["apple",PRODUCE],["apples",PRODUCE],["banana",PRODUCE],["bananas",PRODUCE],["avacado",PRODUCE],["avacados",PRODUCE],["potato",PRODUCE],["potatoes",PRODUCE],["cucumber",PRODUCE],["cucumbers",PRODUCE],["tomato",PRODUCE],["tomatoes",PRODUCE],["carrot",PRODUCE],["carrots",PRODUCE],["kale",PRODUCE],["spinach",PRODUCE],["shampoo",TOILETRIES],["conditioner",TOILETRIES],["soap",TOILETRIES],["body wash",TOILETRIES],["toothpaste",TOILETRIES],["flour",COOKING],["olive oil",COOKING],["vinegar",COOKING],["white vinegar",COOKING],["apple cider vinegar",COOKING],["rice wine vinegar",COOKING],["beef",MEAT],["ground beef",MEAT],["steak",MEAT],["steaks",MEAT],["sausage",MEAT],["sausages",MEAT],["chicken",MEAT],["milk",DAIRY],["cream",DAIRY],["egg",DAIRY],["eggs",DAIRY],["canned beans",CANNED],["canned fruit",CANNED],["canned fruits",CANNED],["canned vegetables",CANNED],["bread",BAKERY],["coffee",BEVERAGE],["tea",BEVERAGE],["frozen beans",CANNED],["frozen fruit",CANNED],["frozen fruits",CANNED],["frozen vegetables",CANNED]]);
