let delimiter="###";window.onload=()=>{let e=document.querySelector("#sortButton");e&&e.addEventListener("click",(()=>{let e=document.querySelector("#inputTextArea").value;document.querySelector("#outputTextArea").value="",document.querySelector("#outputTextArea").value=group(e)}));let t=document.querySelector("#copyButton");t&&t.addEventListener("click",(()=>{navigator.clipboard.writeText(document.querySelector("#outputTextArea").value)}));let r=document.querySelector("#clearButton");r&&r.addEventListener("click",(()=>{confirm("Are you sure you would like to clear your list?")&&(document.querySelector("#inputTextArea").value="",document.querySelector("#outputTextArea").value="")}));let o=document.querySelector("#delimiterSelect");o&&o.addEventListener("change",(()=>{delimiter=o.value}))},group=e=>{let t={produce:`${delimiter} PRODUCE ${delimiter}`,toiletries:`${delimiter} TOILETRIES ${delimiter}`,misc:`${delimiter} MISC ${delimiter}`},r=e.split(/[\n,]/);for(item of r)trimmedItem=item.trim(),trimmedItemToLower=trimmedItem.toLowerCase(),produce.includes(trimmedItemToLower)?t.produce+=`\n${trimmedItem}`:toiletries.includes(trimmedItemToLower)?t.toiletries+=`\n${trimmedItem}`:t.misc+=`\n${trimmedItem}`;return t.produce+"\n\n"+t.toiletries+"\n\n"+t.misc};const produce=["apples","apple","bananas","banana","avacados","avacado","potatoes","potato"],toiletries=["shampoo","conditioner","soap","body wash","toothpaste"];
